mapper.update:
UPDATE orders SET
    order_complete  = #{orders.order_complete,jdbcType=CHAR},
    house_price = #{orders.house_price,jdbcType=FLOAT},
    creat_time    = #{orders.creat_time,jdbcType=VARCHAR}
WHERE order_id = #{orders.order_id}

mapper.select:
UPDATE orders SET
order_complete = #{complete,jdbcType=CHAR}
WHERE user_id = #{user_id} AND house_id = #{house_id}
